import{_ as e,c as t,o as i,a1 as r}from"./chunks/framework.BqN1Ok0Z.js";const m=JSON.parse('{"title":"What is VueJS Tour","description":"","frontmatter":{},"headers":[],"relativePath":"guide/what-is-vuejs-tour.md","filePath":"guide/what-is-vuejs-tour.md"}'),o={name:"guide/what-is-vuejs-tour.md"},s=r('<h1 id="what-is-vuejs-tour" tabindex="-1">What is VueJS Tour <a class="header-anchor" href="#what-is-vuejs-tour" aria-label="Permalink to &quot;What is VueJS Tour&quot;">​</a></h1><p>VueJS Tour is a customizable Vue.js plugin for creating guided tours, leveraging Vue 3&#39;s Composition API and TypeScript for modern, maintainable tours. It supports SASS/SCSS theming for seamless design integration.</p><div class="tip custom-block" style="padding-top:8px;"> Just want to try it out? Skip to the <a href="getting-started">Quickstart</a>. </div><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to &quot;Use Cases&quot;">​</a></h2><ul><li><p><strong>Onboarding New Users</strong></p><p>Introduce new users to your application by guiding them through the key features and functionalities, ensuring a smooth onboarding experience.</p></li><li><p><strong>Feature Highlights</strong></p><p>Use tours to highlight new or underused features to existing users, helping to increase feature adoption and user engagement.</p></li><li><p><strong>User Support</strong></p><p>Reduce support requests by providing interactive guides that answer common questions or guide users through complex processes within the application.</p></li><li><p><strong>Navigation Assistance</strong></p><p>Help users navigate through the application, especially in complex or feature-rich interfaces, improving overall user experience and satisfaction.</p></li><li><p><strong>Educational Content</strong></p><p>Create educational tours that provide users with insights, tips, and best practices related to your application or the services it offers.</p></li></ul><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li><p><strong>Multiple tours</strong></p><p>VueJS Tour supports multiple tours, allowing you to create and manage tours for different sections or features of your application.</p></li><li><p><strong>Modern &amp; Maintainable</strong></p><p>VueJS Tour leverages Vue 3&#39;s Composition API and TypeScript for modern, maintainable tours that are easy to create, update, and maintain.</p></li><li><p><strong>Customizable</strong></p><p>Customize the look and feel of your tours with SASS/SCSS theming, allowing seamless design integration with your application.</p></li><li><p><strong>Interactive</strong></p><p>Engage users with interactive tours that allow them to interact with the application, providing a more immersive and engaging experience.</p></li><li><p><strong>Cross-Browser Compatibility</strong></p><p>VueJS Tour is compatible with all modern browsers, ensuring a consistent experience for all users, regardless of their browser preferences.</p></li><li><p><strong>Lightweight &amp; Performant</strong></p><p>VueJS Tour is lightweight and performant, ensuring that your tours load quickly and run smoothly, without impacting the overall performance of your application.</p></li><li><p><strong>Developer-Friendly</strong></p><p>VueJS Tour is developer-friendly, with comprehensive documentation, examples, and support to help you get started and create amazing tours with ease.</p></li><li><p><strong>Open Source</strong></p><p>VueJS Tour is open source, licensed under the MIT License, allowing you to use, modify, and distribute it freely in your projects.</p></li></ul>',7),a=[s];function n(u,p,l,g,c,d){return i(),t("div",null,a)}const f=e(o,[["render",n]]);export{m as __pageData,f as default};
